import groovy.json.JsonSlurper
def getToken() {
	currentStepId = context.currentStepIndex;
	responseContent = testRunner.testCase.getTestStepByName("auth").getPropertyValue("response");
	token = new JsonSlurper().parseText(responseContent).token;
	testRunner.testCase.setPropertyValue( 'token', token );
	refreshToken = new JsonSlurper().parseText(responseContent).refreshToken;
	testRunner.testCase.setPropertyValue( 'refreshToken', refreshToken );
	log.info (refreshToken)
	log.info (token)
}

getToken()

